"use client"

import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import { Line } from "react-chartjs-2"
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js"
import { APIUrl, handleError, handleSuccess } from "./utils"
import "./home.css"

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend)

function Home() {
  const [loggedInUser, setLoggedInUser] = useState("")
  const [expenses, setExpenses] = useState([])
  const [incomeAmt, setIncomeAmt] = useState(0)
  const [expenseAmt, setExpenseAmt] = useState(0)
  const [balance, setBalance] = useState(0)
  const [text, setText] = useState("")
  const [amount, setAmount] = useState("")
  const [transactionType, setTransactionType] = useState("expense")
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: [],
  })

  const navigate = useNavigate()

  useEffect(() => {
    const user = localStorage.getItem("loggedInUser")
    if (!user) {
      navigate("/login")
      return
    }
    setLoggedInUser(user)
    fetchExpenses()
  }, [navigate])

  useEffect(() => {
    // Calculate totals
    const amounts = expenses.map((item) => item.amount)
    const income = amounts.filter((item) => item > 0).reduce((acc, item) => (acc += item), 0)
    const expense = amounts.filter((item) => item < 0).reduce((acc, item) => (acc += item), 0) * -1

    setIncomeAmt(income)
    setExpenseAmt(expense)
    setBalance(income - expense)

    // Prepare chart data
    prepareChartData()
  }, [expenses])

  const prepareChartData = () => {
    // Get the last 7 transactions for the chart
    const recentTransactions = [...expenses]
      .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
      .slice(0, 7)
      .reverse()

    // Calculate running balance
    let runningBalance = 0
    const balances = recentTransactions.map((transaction) => {
      runningBalance += transaction.amount
      return runningBalance
    })

    // Format dates for labels
    const labels = recentTransactions.map((transaction) => {
      const date = new Date(transaction.createdAt)
      return `${date.getMonth() + 1}/${date.getDate()}`
    })

    setChartData({
      labels,
      datasets: [
        {
          label: "Balance",
          data: balances,
          fill: false,
          borderColor: "#3a86ff",
          tension: 0.1,
        },
      ],
    })
  }

  const handleLogout = () => {
    localStorage.removeItem("token")
    localStorage.removeItem("loggedInUser")
    handleSuccess("Logged out successfully")
    navigate("/login")
  }

  const fetchExpenses = async () => {
    try {
      const url = `${APIUrl}/expenses`
      const headers = {
        headers: {
          Authorization: localStorage.getItem("token"),
        },
      }
      const response = await fetch(url, headers)

      if (response.status === 403) {
        localStorage.removeItem("token")
        navigate("/login")
        return
      }

      const result = await response.json()

      if (result.success) {
        setExpenses(result.data)
      } else {
        handleError(result.message || "Failed to fetch expenses")
      }
    } catch (err) {
      handleError("An error occurred while fetching expenses")
    }
  }

  const addTransaction = async (e) => {
    e.preventDefault()

    if (!text || !amount) {
      handleError("Please enter both description and amount")
      return
    }

    // Convert amount to number and apply sign based on transaction type
    const amountValue = Number.parseFloat(amount)
    const finalAmount = transactionType === "expense" ? -Math.abs(amountValue) : Math.abs(amountValue)

    try {
      const url = `${APIUrl}/expenses`
      const headers = {
        method: "POST",
        headers: {
          Authorization: localStorage.getItem("token"),
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          text,
          amount: finalAmount,
        }),
      }

      const response = await fetch(url, headers)

      if (response.status === 403) {
        localStorage.removeItem("token")
        navigate("/login")
        return
      }

      const result = await response.json()

      if (result.success) {
        setExpenses(result.data)
        setText("")
        setAmount("")
        handleSuccess("Transaction added successfully")
      } else {
        handleError(result.message || "Failed to add transaction")
      }
    } catch (err) {
      handleError("An error occurred while adding transaction")
    }
  }

  const deleteTransaction = async (id) => {
    try {
      const url = `${APIUrl}/expenses/${id}`
      const headers = {
        method: "DELETE",
        headers: {
          Authorization: localStorage.getItem("token"),
        },
      }

      const response = await fetch(url, headers)

      if (response.status === 403) {
        localStorage.removeItem("token")
        navigate("/login")
        return
      }

      const result = await response.json()

      if (result.success) {
        setExpenses(result.data)
        handleSuccess("Transaction deleted successfully")
      } else {
        handleError(result.message || "Failed to delete transaction")
      }
    } catch (err) {
      handleError("An error occurred while deleting transaction")
    }
  }

  return (
    <div className="home-container">
      <nav className="navbar">
        <div className="navbar-brand">Budget Tracker</div>
        <div className="navbar-user">
          <span>Welcome, {loggedInUser}</span>
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </div>
      </nav>

      <div className="dashboard">
        {/* Quadrant 1: Overview */}
        <div className="quadrant overview">
          <h2>Overview</h2>
          <div className="balance-card">
            <h3>Current Balance</h3>
            <div className={`balance-amount ${balance >= 0 ? "positive" : "negative"}`}>${balance.toFixed(2)}</div>
          </div>
          <div className="stats-container">
            <div className="stat-card income">
              <h3>Total Income</h3>
              <div className="stat-amount">${incomeAmt.toFixed(2)}</div>
            </div>
            <div className="stat-card expense">
              <h3>Total Expenses</h3>
              <div className="stat-amount">${expenseAmt.toFixed(2)}</div>
            </div>
          </div>
        </div>

        {/* Quadrant 2: Add Transaction */}
        <div className="quadrant add-transaction">
          <h2>Add Transaction</h2>
          <form onSubmit={addTransaction}>
            <div className="transaction-type">
              <label>
                <input
                  type="radio"
                  name="type"
                  value="income"
                  checked={transactionType === "income"}
                  onChange={() => setTransactionType("income")}
                />
                Income
              </label>
              <label>
                <input
                  type="radio"
                  name="type"
                  value="expense"
                  checked={transactionType === "expense"}
                  onChange={() => setTransactionType("expense")}
                />
                Expense
              </label>
            </div>

            <div className="form-group">
              <label htmlFor="text">Description</label>
              <input
                type="text"
                id="text"
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="Enter description"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="amount">Amount</label>
              <input
                type="number"
                id="amount"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="Enter amount"
                min="0.01"
                step="0.01"
                required
              />
            </div>

            <button type="submit" className="add-button">
              Add Transaction
            </button>
          </form>
        </div>

        {/* Quadrant 3: Balance Chart */}
        <div className="quadrant balance-chart">
          <h2>Balance History</h2>
          <div className="chart-container">
            {expenses.length > 0 ? (
              <Line
                data={chartData}
                options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  scales: {
                    y: {
                      beginAtZero: true,
                    },
                  },
                }}
              />
            ) : (
              <div className="no-data">Add transactions to see your balance history</div>
            )}
          </div>
        </div>

        {/* Quadrant 4: Recent Transactions */}
        <div className="quadrant recent-transactions">
          <h2>Recent Transactions</h2>
          {expenses.length === 0 ? (
            <div className="no-transactions">No transactions yet</div>
          ) : (
            <ul className="transaction-list">
              {expenses
                .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
                .slice(0, 5)
                .map((expense) => (
                  <li key={expense._id} className="transaction-item">
                    <div className="transaction-info">
                      <div className="transaction-text">{expense.text}</div>
                      <div className="transaction-date">{new Date(expense.createdAt).toLocaleDateString()}</div>
                    </div>
                    <div className="transaction-amount-container">
                      <span className={`transaction-amount ${expense.amount < 0 ? "negative" : "positive"}`}>
                        {expense.amount < 0 ? "-" : "+"}${Math.abs(expense.amount).toFixed(2)}
                      </span>
                      <button onClick={() => deleteTransaction(expense._id)} className="delete-button">
                        ×
                      </button>
                    </div>
                  </li>
                ))}
            </ul>
          )}
          <button
            onClick={() => (document.getElementById("all-transactions-modal").style.display = "block")}
            className="view-all-button"
          >
            View All Transactions
          </button>

          {/* Modal for all transactions */}
          <div id="all-transactions-modal" className="modal">
            <div className="modal-content">
              <span
                className="close-button"
                onClick={() => (document.getElementById("all-transactions-modal").style.display = "none")}
              >
                &times;
              </span>
              <h2>All Transactions</h2>
              {expenses.length === 0 ? (
                <div className="no-transactions">No transactions yet</div>
              ) : (
                <ul className="transaction-list full-list">
                  {expenses
                    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
                    .map((expense) => (
                      <li key={expense._id} className="transaction-item">
                        <div className="transaction-info">
                          <div className="transaction-text">{expense.text}</div>
                          <div className="transaction-date">{new Date(expense.createdAt).toLocaleDateString()}</div>
                        </div>
                        <div className="transaction-amount-container">
                          <span className={`transaction-amount ${expense.amount < 0 ? "negative" : "positive"}`}>
                            {expense.amount < 0 ? "-" : "+"}${Math.abs(expense.amount).toFixed(2)}
                          </span>
                          <button
                            onClick={() => {
                              deleteTransaction(expense._id)
                              document.getElementById("all-transactions-modal").style.display = "none"
                            }}
                            className="delete-button"
                          >
                            ×
                          </button>
                        </div>
                      </li>
                    ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Home
