import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { APIUrl, handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import ExpenseTable from './ExpenseTable';
import ExpenseDetails from './ExpenseDetails';
import ExpenseForm from './ExpenseForm';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import './Login.css';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [expenses, setExpenses] = useState([]);
    const [incomeAmt, setIncomeAmt] = useState(0);
    const [expenseAmt, setExpenseAmt] = useState(0);
    const [transactionType, setTransactionType] = useState('expense'); // 'income' or 'expense'
    const [chartData, setChartData] = useState([]);

    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
    }, []);

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        setTimeout(() => {
            navigate('/login');
        }, 1000);
    };

    useEffect(() => {
        const amounts = expenses.map(item => item.amount);
        const income = amounts.filter(item => item > 0)
            .reduce((acc, item) => (acc += item), 0);
        const exp = amounts.filter(item => item < 0)
            .reduce((acc, item) => (acc += item), 0) * -1;
        setIncomeAmt(income);
        setExpenseAmt(exp);

        // Generate chart data
        const last7Transactions = [...expenses]
            .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
            .slice(0, 7);

        let balance = 0;
        const chartPoints = last7Transactions.reverse().map(item => {
            balance += item.amount;
            return {
                name: new Date(item.createdAt).toLocaleDateString(),
                balance: balance
            };
        });

        setChartData(chartPoints);
    }, [expenses]);

    const deleteExpens = async (id) => {
        try {
            const url = `http://localhost:8080/expenses/${id}`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                },
                method: "DELETE"
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            handleSuccess(result?.message);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    const fetchExpenses = async () => {
        try {
            const url = `http://localhost:8080/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                }
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    const addTransaction = async (data) => {
        try {
            // Handle positive or negative amount based on transaction type
            const modifiedData = {
                ...data,
                amount: transactionType === 'expense' ? -Math.abs(data.amount) : Math.abs(data.amount)
            };

            const url = `http://localhost:8080/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    'Content-Type': 'application/json'
                },
                method: "POST",
                body: JSON.stringify(modifiedData)
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            handleSuccess(result?.message);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    useEffect(() => {
        fetchExpenses();
    }, []);

    return (
        <div className="expense-tracker-app">
            {/* Navbar */}
            <nav className="navbar">
                <div className="company-name">ExpenseTracker Pro</div>
                <div className="user-section">
                    <span className="username">Welcome, {loggedInUser}</span>
                    <button className="logout-btn" onClick={handleLogout}>Logout</button>
                </div>
            </nav>

            {/* Main content divided into 4 quadrants */}
            <div className="quadrants-container">
                {/* Quadrant 1: Overview */}
                <div className="quadrant overview">
                    <h2>Financial Overview</h2>
                    <ExpenseDetails
                        incomeAmt={incomeAmt}
                        expenseAmt={expenseAmt}
                    />
                </div>

                {/* Quadrant 2: Add Transaction */}
                <div className="quadrant add-transaction">
                    <h2>Add New Transaction</h2>
                    <div className="transaction-type-selector">
                        <button 
                            className={`type-btn ${transactionType === 'income' ? 'active' : ''}`}
                            onClick={() => setTransactionType('income')}
                        >
                            Income
                        </button>
                        <button 
                            className={`type-btn ${transactionType === 'expense' ? 'active' : ''}`}
                            onClick={() => setTransactionType('expense')}
                        >
                            Expense
                        </button>
                    </div>
                    <ExpenseForm
                        addTransaction={addTransaction}
                        transactionType={transactionType}
                    />
                </div>

                {/* Quadrant 3: Balance Chart */}
                <div className="quadrant balance-chart">
                    <h2>Balance History</h2>
                    <ResponsiveContainer width="100%" height={250}>
                        <LineChart
                            data={chartData}
                            margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
                        >
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Line 
                                type="monotone" 
                                dataKey="balance" 
                                stroke="#8884d8" 
                                activeDot={{ r: 8 }} 
                                strokeWidth={2}
                            />
                        </LineChart>
                    </ResponsiveContainer>
                </div>

                {/* Quadrant 4: Recent Transactions */}
                <div className="quadrant recent-transactions">
                    <h2>Recent Transactions</h2>
                    <ExpenseTable
                        expenses={expenses.slice(0, 5)} // Show only the 5 most recent transactions
                        deleteExpens={deleteExpens}
                    />
                    {expenses.length > 5 && (
                        <button className="view-all-btn">View All Transactions</button>
                    )}
                </div>
            </div>
            <ToastContainer />
        </div>
    );
}

export default Home;
