import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { APIUrl, handleError, handleSuccess } from '../utils';
import { ToastContainer } from 'react-toastify';
import ExpenseTable from './ExpenseTable';
import ExpenseDetails from './ExpenseDetails';
import ExpenseForm from './ExpenseForm';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import './Login.css';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState('');
    const [expenses, setExpenses] = useState([]);
    const [incomeAmt, setIncomeAmt] = useState(0);
    const [expenseAmt, setExpenseAmt] = useState(0);
    const [transactionType, setTransactionType] = useState('expense'); // 'income' or 'expense'
    const [chartData, setChartData] = useState([]);
    const [activeFilter, setActiveFilter] = useState('all');

    const navigate = useNavigate();

    useEffect(() => {
        setLoggedInUser(localStorage.getItem('loggedInUser'));
    }, []);

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('loggedInUser');
        handleSuccess('User Logged out');
        setTimeout(() => {
            navigate('/login');
        }, 1000);
    };

    useEffect(() => {
        const amounts = expenses.map(item => item.amount);
        const income = amounts.filter(item => item > 0)
            .reduce((acc, item) => (acc += item), 0);
        const exp = amounts.filter(item => item < 0)
            .reduce((acc, item) => (acc += item), 0) * -1;
        setIncomeAmt(income);
        setExpenseAmt(exp);

        // Generate chart data
        const last7Transactions = [...expenses]
            .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
            .slice(0, 10);

        let balance = income - exp;
        const chartPoints = last7Transactions.reverse().map(item => {
            balance = balance - item.amount;
            return {
                name: new Date(item.createdAt).toLocaleDateString(),
                amount: balance
            };
        });

        // Reverse again to get chronological order
        setChartData([...chartPoints].reverse());
    }, [expenses]);

    const deleteExpens = async (id) => {
        try {
            const url = `http://localhost:8080/expenses/${id}`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                },
                method: "DELETE"
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            handleSuccess(result?.message);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    const fetchExpenses = async () => {
        try {
            const url = `http://localhost:8080/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token')
                }
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    const addTransaction = async (data) => {
        try {
            // Handle positive or negative amount based on transaction type
            const modifiedData = {
                ...data,
                amount: transactionType === 'expense' ? -Math.abs(data.amount) : Math.abs(data.amount)
            };

            const url = `http://localhost:8080/expenses`;
            const headers = {
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    'Content-Type': 'application/json'
                },
                method: "POST",
                body: JSON.stringify(modifiedData)
            };
            const response = await fetch(url, headers);
            if (response.status === 403) {
                localStorage.removeItem('token');
                navigate('/login');
                return;
            }
            const result = await response.json();
            handleSuccess(result?.message);
            setExpenses(result.data);
        } catch (err) {
            handleError(err);
        }
    };

    useEffect(() => {
        fetchExpenses();
    }, []);

    const filteredExpenses = expenses.filter(expense => {
        if (activeFilter === 'all') return true;
        if (activeFilter === 'income') return expense.amount > 0;
        if (activeFilter === 'expense') return expense.amount < 0;
        return true;
    }).slice(0, 5);

    return (
        <div className="expense-tracker-app">
            {/* Navbar */}
            <div className="navbar">
                <div className="company-name">XpenseXpert</div>
                <div className="user-section">
                    <span className="username">{loggedInUser}</span>
                    <button className="logout-btn" onClick={handleLogout}>Logout</button>
                </div>
            </div>

            {/* Main Content */}
            <div className="main-content">
                {/* Main content divided into 4 quadrants */}
                <div className="quadrants-container">
                    {/* Quadrant 1: Financial Summary */}
                    <div className="quadrant overview">
                        <h2>Financial Summary</h2>
                        <div className="balance-summary">
                            <div className="balance-card">
                                <div className="label">Balance</div>
                                <div className="amount balance-amount">₹{(incomeAmt - expenseAmt).toFixed(2)}</div>
                            </div>
                            <div className="balance-card">
                                <div className="label">Income</div>
                                <div className="amount income-amount">₹{incomeAmt.toFixed(2)}</div>
                            </div>
                            <div className="balance-card">
                                <div className="label">Expenses</div>
                                <div className="amount expense-amount">₹{expenseAmt.toFixed(2)}</div>
                            </div>
                        </div>
                    </div>

                    {/* Quadrant 2: Financial Overview Chart */}
                    <div className="quadrant balance-chart">
                        <h2>Financial Overview</h2>
                        <div className="chart-controls">
                            <button className="chart-btn active">Line Chart</button>
                            <button className="chart-btn">Bar Chart</button>
                        </div>
                        <ResponsiveContainer width="100%" height={220}>
                            <LineChart
                                data={chartData}
                                margin={{ top: 5, right: 5, left: 5, bottom: 5 }}
                            >
                                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                                <XAxis dataKey="name" tick={{ fontSize: 12 }} />
                                <YAxis tick={{ fontSize: 12 }} />
                                <Tooltip />
                                <Line 
                                    type="monotone" 
                                    dataKey="amount" 
                                    stroke="#304ffe" 
                                    activeDot={{ r: 8 }} 
                                    strokeWidth={2}
                                    dot={{ stroke: '#304ffe', strokeWidth: 2, r: 4 }}
                                />
                            </LineChart>
                        </ResponsiveContainer>
                    </div>

                    {/* Quadrant 3: Add Transaction */}
                    <div className="quadrant add-transaction">
                        <h2>Add Transaction</h2>
                        <div className="transaction-type-selector">
                            <button 
                                className={`type-btn ${transactionType === 'income' ? 'active' : ''}`}
                                onClick={() => setTransactionType('income')}
                            >
                                Income
                            </button>
                            <button 
                                className={`type-btn ${transactionType === 'expense' ? 'active' : ''}`}
                                onClick={() => setTransactionType('expense')}
                            >
                                Expense
                            </button>
                        </div>
                        <ExpenseForm
                            addTransaction={addTransaction}
                            transactionType={transactionType}
                        />
                    </div>

                    {/* Quadrant 4: Transaction History */}
                    <div className="quadrant recent-transactions">
                        <h2>Transaction History</h2>
                        <div className="transaction-filters">
                            <button 
                                className={`filter-btn ${activeFilter === 'all' ? 'active' : ''}`}
                                onClick={() => setActiveFilter('all')}
                            >
                                All
                            </button>
                            <button 
                                className={`filter-btn ${activeFilter === 'income' ? 'active' : ''}`}
                                onClick={() => setActiveFilter('income')}
                            >
                                Income
                            </button>
                            <button 
                                className={`filter-btn ${activeFilter === 'expense' ? 'active' : ''}`}
                                onClick={() => setActiveFilter('expense')}
                            >
                                Expenses
                            </button>
                        </div>

                        <div className="expense-list">
                            {filteredExpenses.map((expense, index) => (
                                <div key={index} className={`expense-item ${expense.amount > 0 ? 'income' : 'expense'}`}>
                                    <div className="expense-left">
                                        <div className="expense-icon">
                                            {expense.amount > 0 ? '+' : '-'}
                                        </div>
                                        <div className="expense-details">
                                            <div className="expense-title">{expense.text}</div>
                                            <div className="expense-date">
                                                {new Date(expense.createdAt || Date.now()).toLocaleDateString()}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="expense-amount">
                                        {expense.amount > 0 ? '+' : ''}₹{Math.abs(expense.amount)}
                                    </div>
                                    <button className="delete-button" onClick={() => deleteExpens(expense._id)}>
                                        ×
                                    </button>
                                </div>
                            ))}
                        </div>

                        {expenses.length > 5 && (
                            <button className="view-all-btn">View All Transactions</button>
                        )}
                    </div>
                </div>
            </div>
            <ToastContainer />
        </div>
    );
}

export default Home;
